void dfs(int x, int fa, int op){
	dep[x][op] = dep[fa][op] + 1;
	if (op == 1) par[x][0] = fa;
	for (int v : p[x][op]) if (v != fa){
		dfs(v, x, op);
	}
}
void init(){
	for (int i = 1; i < 20; i++){
		for (int j = 1; j <= n; j++){
			par[j][i] = par[par[j][i - 1]][i - 1];
		}
	}
}
int lca(int u, int v){
	if (dep[u][1] < dep[v][1]) swap(u, v);
	for (int i = 0; i < 20; i++) if ((dep[u][1] - dep[v][1]) >> i & 1) u = par[u][i];
	if (u == v) return u;
	for (int i = 19; i >= 0; i--) if (par[u][i] != par[v][i]) u = par[u][i], v = par[v][i];
	return par[u][0]; 
}
int dis(int u, int v){
	int lc = lca(u, v);
	return dep[u][1] + dep[v][1] - 2 * dep[lc][1];
}
void ddfs(int x, int fa){
	if (ans != -1) ans = max(ans, (dep[x][1] - 1) * 2);
	for (int v : p[x][0]){
		if (dis(x, v) >= 3) ans = -1;
	}

	for (int v : p[x][0]) if (v != fa){
		if (dep[v][0] < dep[v][1]){
			ddfs(v, x);			
		}	
	}
}

