	for(int nxt : blue[cur]){
		if(nxt == par) continue;
		dist_blue[nxt] = dist_blue[cur] + 1;
		dfs(nxt, cur);
	}
}
int mx;
void loop(){
	cout << -1 << endl;
	exit(0);
}
void run(int cur, int par, int i){
	if(dist_blue[cur] <= i) mx = max(mx, dist_blue[cur] * 2);
	else {
		if(good[cur]) loop();
		for(int nxt : red[cur]){
			if(nxt == par) continue;
			run(nxt, cur, i + 1);
		}
	}
}
