vector<int> depth, pv, pe, pre, endpre, preseq, siz;
int logn;
vector<vector<int>> anc;
struct Jump {
	int sum;
	void init(int v) {
		Edge& e = edges[pe[v]];
		sum = 1;
	}
	void combine(const Jump& x, const Jump& a) { // a is ancestor of x
		sum = x.sum + a.sum;
	}
};
vector<vector<Jump>> jump;
void dfs(int x, int p = -1) {
	siz[x] = 1;
	pre[x] = preseq.size();
	preseq.push_back(x);
	for (auto& e : adj[x]) {
		int y = e.first, eid = e.second;
		if (y == p) continue;
		depth[y] = depth[x] + 1;
		pv[y] = x;
		pe[y] = eid;
		dfs(y, x);
		siz[x] += siz[y];
	}
	endpre[x] = (int)preseq.size() - 1; // This is the last vertex in the subtree of x
}
void init(int root = 1) {
	siz = vector<int>(n + 1);
	depth = vector<int>(n + 1);
