	preseq.clear();
	dfs(root);
	logn = 31 - __builtin_clz(n);
	anc = vector<vector<int>>(n + 1, vector<int>(logn + 1));
	for (int i = 1; i <= n; ++i) anc[i][0] = pv[i];
	for (int p = 1; p <= logn; ++p)
		for (int i = 1; i <= n; ++i)
			anc[i][p] = anc[i][p - 1] == -1 ? -1 : anc[anc[i][p - 1]][p - 1];
