bool isAnc(int a, int b) {
	return pre[a] <= pre[b] && endpre[a] >= endpre[b];
}
int getChildTowards(int a, int sub) {
	int dh = depth[sub] - depth[a] - 1;
	for (int p = logn; p >= 0; --p)
		if (dh & (1 << p)) sub = anc[sub][p];
	return sub;
}
int getLca(int a, int b) {
	if (isAnc(a, b)) return a;
	if (isAnc(b, a)) return b;
	for (int p = logn; p >= 0; --p)
		if (anc[a][p] != -1 && !isAnc(anc[a][p], b))
			a = anc[a][p];
	a = anc[a][0];
	return a;
}
// Jump up from a to ancestor, running f on each 2^k-block in order.
void jumpUp(int a, int ancestor, function<void(Jump&)> f) {
	assert(isAnc(ancestor, a));
	int up = depth[a] - depth[ancestor];
	for (int p = logn; p >= 0; --p)
		if (up & (1 << p)) {
			f(jump[a][p]);
			a = anc[a][p];
		}
}
vector<int> chainRoot, chainBottom;
void dfsSiz(int x, int p = -1) {
	for (auto it = adj[x].begin(); it != adj[x].end(); ++it)
		if (it->first == p) {
			adj[x].erase(it);
			break;
		}
	siz[x] = 1;
	for (auto& e : adj[x]) {
		int y = e.first;
		dfsSiz(y, x);
		siz[x] += siz[y];
		if (siz[y] > siz[adj[x][0].first]) swap(e, adj[x][0]);
	}
}
void dfsHld(int x, int hldroot) {
	chainRoot[x] = hldroot;
	chainBottom[hldroot] = x;
	for (auto& e : adj[x]) {
		int y = e.first;
		dfsHld(y, y == adj[x][0].first ? hldroot : y);
	}
}
struct Chain {
	vector<int> vs;
	// Bottom vertex and top vertex of chain
	void init(int bot, int top) {
		vs.clear();
		for (int x = bot; x != pv[top]; x = pv[x])
			vs.push_back(x);
	}
};
vector<Chain> chains;
// Move upwards from x to y (ancestor)
// A segment consists of vertices on a heavy path. pre[x] stores the position in the
// heavy-light segment tree of the edge (x, parent[x]).
// Smaller pre -> higher up in tree (lower depth)
// Apply f(Chain, vertex_bottom, vertex_top) on some prefix of the chain
void moveUp(int x, int y, function<void(Chain&, int, int)> f) {
	for (;;) {
		int z = chainRoot[x];
		bool isLastChain = z == chainRoot[y];
		f(chains[chainRoot[x]], x, isLastChain ? y : z);
		if (isLastChain) break;
		x = pv[z];
	}
}
// Chains are contiguous substrings of preSeq array.
// Or, we also have a struct Chain in each chainRoot.
void initHld(int root = 1) {
	siz = vector<int>(n + 1);
	dfsSiz(root); // Make adj[x][0] the largest subtree
	init(root); // Now pre and endpre is on the hld sequence
	chainRoot = vector<int>(n + 1);
	chainBottom = vector<int>(n + 1);
	dfsHld(root, root);
	chains = vector<Chain>(n + 1);
	for (int i = 1; i <= n; ++i) {
		if (chainRoot[i] != i) continue;
		chains[i].init(chainBottom[i], i);
	}
}
