using namespace tree;
int getDist(int a, int b) {
	int c = getLca(a, b);
	return depth[a] + depth[b] - 2 * depth[c];
}
vector<int> EX[200005];
int maxt;
void go(int x, int p = -1, int h = 0) {
	if (depth[x] <= h) return;
	maxt = max(maxt, depth[x]);
	for (int y : EX[x]) {
		if (y == p) continue;
		if (getDist(x, y) > 2) {
			printf("-1\n");
			exit(0);
		}
		go(y, x, h + 1);
	}
}
