        public void solve(int testNumber, FastInput in, PrintWriter out) {
            int n = in.readInt();

            Node[] nodes = new Node[n + 1];
            LCTNode[] lctNodes = new LCTNode[n + 1];
            for (int i = 1; i <= n; i++) {
                nodes[i] = new Node();
                nodes[i].id = i;
                lctNodes[i] = new LCTNode();
                lctNodes[i].pushUp();
