            }

            dfsForDepth(y, null, 0);

            for (Node[] edge : edges) {
                LCTNode.findRoute(lctNodes[edge[0].id], lctNodes[edge[1].id]);
                LCTNode.splay(lctNodes[edge[0].id]);
                if (lctNodes[edge[0].id].size - 1 >= 3) {
                    edge[0].escape = edge[1].escape = true;
                }
            }

            int escape = escape(x, null, 0);
            if (escape == inf) {
