    static class LCTNode {
        public static final LCTNode NIL = new LCTNode();
        LCTNode left = NIL;
        LCTNode right = NIL;
        LCTNode father = NIL;
        LCTNode treeFather = NIL;
        boolean reverse;
        int id;
        int size;
        static {
            NIL.left = NIL;
            NIL.right = NIL;
            NIL.father = NIL;
            NIL.treeFather = NIL;
        }

        public static void access(LCTNode x) {
            LCTNode last = NIL;
            while (x != NIL) {
                splay(x);
                x.right.father = NIL;
                x.right.treeFather = x;
                x.setRight(last);
                x.pushUp();

                last = x;
                x = x.treeFather;
            }
        }

        public static void makeRoot(LCTNode x) {
            access(x);
            splay(x);
            x.reverse();
        }

        public static void join(LCTNode y, LCTNode x) {
            makeRoot(x);
            x.treeFather = y;
        }

        public static void findRoute(LCTNode x, LCTNode y) {
            makeRoot(y);
            access(x);
        }

        public static void splay(LCTNode x) {
            if (x == NIL) {
                return;
            }
            LCTNode y, z;
            while ((y = x.father) != NIL) {
                if ((z = y.father) == NIL) {
                    y.pushDown();
                    x.pushDown();
                    if (x == y.left) {
                        zig(x);
                    } else {
                        zag(x);
                    }
                } else {
                    z.pushDown();
                    y.pushDown();
                    x.pushDown();
                    if (x == y.left) {
                        if (y == z.left) {
                            zig(y);
                            zig(x);
                        } else {
                            zig(x);
                            zag(x);
                        }
                    } else {
                        if (y == z.left) {
                            zag(x);
                            zig(x);
                        } else {
                            zag(y);
                            zag(x);
                        }
                    }
                }
            }

            x.pushDown();
            x.pushUp();
        }

        public static void zig(LCTNode x) {
            LCTNode y = x.father;
            LCTNode z = y.father;
            LCTNode b = x.right;

            y.setLeft(b);
            x.setRight(y);
            z.changeChild(y, x);

            y.pushUp();
        }

        public static void zag(LCTNode x) {
            LCTNode y = x.father;
            LCTNode z = y.father;
            LCTNode b = x.left;

            y.setRight(b);
            x.setLeft(y);
            z.changeChild(y, x);

            y.pushUp();
        }

        public String toString() {
            return "" + id;
        }

        public void pushDown() {
            if (reverse) {
                reverse = false;

                LCTNode tmpNode = left;
                left = right;
                right = tmpNode;

                left.reverse();
                right.reverse();
            }

            left.treeFather = treeFather;
            right.treeFather = treeFather;
        }

        public void reverse() {
            reverse = !reverse;
        }

        public void setLeft(LCTNode x) {
            left = x;
            x.father = this;
        }

        public void setRight(LCTNode x) {
            right = x;
            x.father = this;
        }

        public void changeChild(LCTNode y, LCTNode x) {
            if (left == y) {
                setLeft(x);
            } else {
                setRight(x);
            }
        }

        public void pushUp() {
            if (this == NIL) {
                return;
            }
            size = 1 + left.size + right.size;
        }

    }
}

import java.io.*;
import java.util.*;

public class Main {

	BufferedReader br;
	PrintWriter out;
	StringTokenizer st;
	boolean eof;

	List<Integer>[] g1, g2;
	int n;

	static final int INF = 1_000_000_000;

	int[][] bfs(List<Integer>[] g, int root) {
		int[] dist = new int[n];
		Arrays.fill(dist, INF);
		int[] par = new int[n];
		par[root] = root;
		int[] q = new int[n];
		int head = 0, tail = 0;
		q[tail++] = root;
		dist[root] = 0;

		while (head < tail) {
			int v = q[head++];
			for (int u : g[v]) {
				if (dist[u] > dist[v] + 1) {
					dist[u] = dist[v] + 1;
					par[u] = v;
					q[tail++] = u;
				}
			}
		}

		return new int[][] { dist, par };
	}

	boolean badEdge(int v, int u, int[] par) {
		return (par[v] == u || par[u] == v) || // distance 1
				(par[par[v]] == u || par[par[u]] == v || par[v] == par[u]); // distance
																			// 2
	}

	void solve() throws IOException {
		n = nextInt();
		int x = nextInt() - 1;
		int y = nextInt() - 1;

		g1 = new List[n];
		for (int i = 0; i < n; i++) {
			g1[i] = new ArrayList<>();
		}

		g2 = new List[n];
		for (int i = 0; i < n; i++) {
			g2[i] = new ArrayList<>();
		}

		for (int i = 0; i < n - 1; i++) {
			int v = nextInt() - 1;
			int u = nextInt() - 1;
			g1[v].add(u);
			g1[u].add(v);
		}

		for (int i = 0; i < n - 1; i++) {
			int v = nextInt() - 1;
			int u = nextInt() - 1;
			g2[v].add(u);
			g2[u].add(v);
		}

		int[][] info1 = bfs(g1, x);
		int[][] info2 = bfs(g2, y);

//		System.err.println(Arrays.toString(info2[1]));

		boolean[] safeNodes = new boolean[n];
		for (int v = 0; v < n; v++) {
			for (int u : g1[v]) {
				if (u < v) {
					continue;
				}

//				System.err.println(v + " " + u + " " + badEdge(v, u, info2[1]));

				if (!badEdge(v, u, info2[1])) {
					safeNodes[v] = safeNodes[u] = true;
				}
			}
		}

		int[] dist = new int[n];
		Arrays.fill(dist, INF);
		int[] q = new int[n];
		int head = 0, tail = 0;
		q[tail++] = x;
		dist[x] = 0;

		int ans = 0;

		while (head < tail) {
			int v = q[head++];
			if (safeNodes[v]) {
				out.println(-1);
				return;
			}
			ans = Math.max(ans, info2[0][v]);
			for (int u : g1[v]) {
				if (info1[0][u] >= info2[0][u]) {
					continue;
				}
				if (dist[u] > dist[v] + 1) {
					dist[u] = dist[v] + 1;
					q[tail++] = u;
				}
			}
		}

		out.println(ans * 2);

	}

	Main() throws IOException {
		br = new BufferedReader(new InputStreamReader(System.in));
		out = new PrintWriter(System.out);
		solve();
		out.close();
	}

	public static void main(String[] args) throws IOException {
		new Main();
	}

	String nextToken() {
		while (st == null || !st.hasMoreTokens()) {
			try {
				st = new StringTokenizer(br.readLine());
			} catch (Exception e) {
				eof = true;
				return null;
			}
		}
		return st.nextToken();
	}

	String nextString() {
		try {
			return br.readLine();
		} catch (IOException e) {
			eof = true;
			return null;
		}
	}

	int nextInt() throws IOException {
		return Integer.parseInt(nextToken());
	}

	long nextLong() throws IOException {
		return Long.parseLong(nextToken());
	}

	double nextDouble() throws IOException {
		return Double.parseDouble(nextToken());
	}
}

