            ans *= 2;
        }
        sb.Append(ans+"\n");
    }
    void dfs(int v,int d){
        b[v] = true;
        if(depth[v] < d){
            return;
        }
        if(ans != -1 && ans < depth[v]){
            ans = depth[v];
        }
        if(depth[v] > d){
            if(check(v)){
                ans = -1;
                return;
            }
            for(int i=0;i<G[v].Count;i++){
                int t = G[v][i];
                if(!b[t]){
                    dfs(t,d+1);
                }
            }
        }
    }
    bool check(int v){
        for(int i=0;i<G[v].Count;i++){
            int t = G[v][i];
            if(parent[v] == parent[t] || (parent[v] != -1 && parent[parent[v]] == t) || (parent[t] != -1 && parent[parent[t]] == v) || parent[t] == v || parent[v] == t){
                ;         
            }
            else{
                return true;
            }
        }
        return false;
    }
}
public class Tree{
    List<int>[] T;
    bool[] b;
    int N;
    int temp1;
    int temp2;
    int[] depth;
    int[] parent;
    public int center1;
    public int center2;
    public int diamater;
    public Tree(List<int>[] G){
        T = G;
        N = G.Length;
    } 
    void calcLongestPath(int v){
        b = new bool[N];
        temp1 = 0;
        temp2 = 0;
        dfs1(v,0);
    }
    public int LongestPathDistance(int v){
        calcLongestPath(v);
        return temp1;
    }
    public int LongestPath(int v){
        calcLongestPath(v);
        return temp2;
    }
    public void Diamater(){
        int x1 = LongestPath(0);
        int x2 = LongestPath(x1);
        diamater = temp1;
    }
    public void Center(){
        int x1 = LongestPath(0);
        int x2 = LongestPath(x1);
        diamater = temp1;
        if(diamater % 2 == 0){
            center1 = Search(x1,x2,diamater/2);
            center2 = -1;
        }
        else{
            center1 = Search(x1,x2,diamater/2);
            center2 = Search(x1,x2,diamater/2+1);
        }
    }
    public int Search(int s,int t,int l){
        b = new bool[N];
        dfs2(s,t,0,l);
        return temp1;
    }
    public int[] GetDepth(int p){
        b = new bool[N];
        depth = new int[N];
        dfs3(p,0);
        return depth;
    }
    public int[] GetParent(int p){
        b = new bool[N];
        parent = new int[N];
        dfs4(p,-1);
        return parent;
    }
    void dfs1(int v,int l){
        b[v] = true;
        for(int i=0;i<T[v].Count;i++){
            int t = T[v][i];
            if(!b[t]){
                dfs1(t,l+1);
            }
        }
        if(l >= temp1){
            temp1 = l;
            temp2 = v;
        }
    }
    bool dfs2(int v,int o,int l,int d){
        b[v] = true;
        bool x = v == o;
        for(int i=0;i<T[v].Count;i++){
            int t = T[v][i];
            if(!b[t]){
                x |= dfs2(t,o,l+1,d);
            }
        }
        if(x && l == d){
            temp1 = v;
        }
        return x;
    }
    void dfs3(int v,int d){
        b[v] = true;
        depth[v] = d;
        for(int i=0;i<T[v].Count;i++){
            int t = T[v][i];
            if(!b[t]){
                dfs3(t,d+1);
            }
        }
    }
    void dfs4(int v,int p){
        b[v] = true;
        parent[v] = p;
        for(int i=0;i<T[v].Count;i++){
            int t = T[v][i];
            if(!b[t]){
                dfs4(t,v);
            }
        }
    }
}

using System;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Collections.Generic;
using System.Diagnostics;
using Enu = System.Linq.Enumerable;

public class Program
{
    public void Solve()
    {
        int N = NextInt(), A = NextInt() - 1, B = NextInt() - 1;
        var E = new List<int>[2][];

        for (int who = 0; who < 2; who++)
        {
            E[who] = new List<int>[N];
            for (int i = 0; i < N; i++)
                E[who][i] = new List<int>();
            for (int i = 0; i < N - 1; i++)
            {
                int a = NextInt() - 1, b = NextInt() - 1;
                E[who][a].Add(b);
                E[who][b].Add(a);
            }
        }
        var lca = new LowestCommonAncestor(B, E[1]);
        var seen = new bool[N];
        var que = new Queue<int>();
        que.Enqueue(A);
        int ans = lca.Depth(A) * 2;

        for (int steps = 1; que.Count > 0; steps++)
        {
            var nextQue = new Queue<int>();
            while (que.Count > 0)
            {
                int at = que.Dequeue();
                foreach (int next in E[0][at])
                    if (!seen[next])
                    {
                        if (lca.Dist(at, next) > 2) { Console.WriteLine(-1); return; }
                        seen[next] = true;
                        int nextDepth = lca.Depth(next);
                        if (nextDepth >= steps) ans = Math.Max(ans, nextDepth * 2);
                        if (nextDepth > steps) nextQue.Enqueue(next);
                    }
            }
            que = nextQue;
        }

        Console.WriteLine(ans);
    }

    TextReader _reader = Console.In;
    int NextInt()
    {
        int c;
        while ((c = _reader.Read()) < '0' || c > '9') { }
        int res = c - '0';
        while ((c = _reader.Read()) >= '0' && c <= '9') res = res * 10 + c - '0';
        return res;
    }

    class LowestCommonAncestor
    {
        List<int>[] G;
        RangeMinQueryIndex Rmq;
        List<int> Vs = new List<int>();
        List<int> depth = new List<int>();
        int[] Id, depthAt;

        public LowestCommonAncestor(int root, List<int>[] G)
        {
            this.G = G;
            Init(root);
        }
        public int Get(int a, int b)
        {
            int L = Math.Min(Id[a], Id[b]);
            int R = Math.Max(Id[a], Id[b]) + 1;
            int i = Rmq.Get(L, R);
            return Vs[i];
        }
        public int Depth(int a) { return depthAt[a]; }
        public int Dist(int a, int b)
        {
            return depthAt[a] + depthAt[b] - depthAt[Get(a, b)] * 2;
        }

        void Init(int root)
        {
            Id = new int[G.Length];
            depthAt = new int[G.Length];
            DFS(root, -1, 0);
            Rmq = new RangeMinQueryIndex(depth.Count);
            for (int i = 0; i < depth.Count; i++)
                Rmq.Set(i, depth[i]);
        }
        void DFS(int v, int prev, int d)
        {
            Id[v] = Vs.Count;
            Vs.Add(v);
            depth.Add(d);
            depthAt[v] = d;
            foreach (int next in G[v])
                if (next != prev)
                {
                    DFS(next, v, d + 1);
                    Vs.Add(v);
                    depth.Add(d);
                }
        }
    }

    class RangeMinQueryIndex
    {
        private static readonly int INF = (int)1e9;
        public readonly int N;
        private readonly int[] tree;
        private readonly int[] index;

        public RangeMinQueryIndex(int N)
        {
            while (N < 2 || (N & (N - 1)) != 0) { N += N & -N; }
            this.N = N;
            tree = new int[N * 2];
            index = new int[N * 2];
            for (int i = 0; i < tree.Length; i++) tree[i] = INF;
        }
        public void Set(int i, int val)
        {
            int id = i;
            i += N - 1;
            tree[i] = val;
            index[i] = id;
            while (i > 0)
            {
                i = (i - 1) / 2;
                int L = i * 2 + 1, R = i * 2 + 2;
                if (tree[L] <= tree[R]) { tree[i] = tree[L]; index[i] = index[L]; }
                else { tree[i] = tree[R]; index[i] = index[R]; }
            }
        }
        // [L, R)
        public int Get(int L, int R) { int v = -1; return Rec(0, ref v, 0, N, L, R); }
        private int Rec(int i, ref int val, int currL, int currR, int L, int R)
        {
            if (currL >= R || currR <= L) { val = INF; return -1; }
            if (currL >= L && currR <= R) { val = tree[i]; return index[i]; }
            int mid = (currL + currR) / 2;
            int Lv = INF, Rv = INF;
            int Li = Rec(i * 2 + 1, ref Lv, currL, mid, L, R);
            int Ri = Rec(i * 2 + 2, ref Rv, mid, currR, L, R);
            if (Lv <= Rv) { val = Lv; return Li; }
            val = Rv; return Ri;
        }
    }

}


