typedef long long LL;
const int SIZE = 11;
int n;
LL D[SIZE], V[SIZE];
LL T[SIZE];

void solve() {
    for ( int i = 0; i < n; ++ i ) {
        LL t = gcd( D[i], V[i] );
        D[i] /= t;
        V[i] /= t;
    }
    LL vgcd = V[0];
    for ( int i = 1; i < n; ++ i ) {
        vgcd = gcd( vgcd, V[i] );
    }
    LL dlcm = D[0];
    for ( int i = 1; i < n; ++ i ) {
        dlcm = lcm( dlcm, D[i] );
    }
    for ( int i = 0; i < n; ++ i ) {
        cout << (LL)( (long double)dlcm / vgcd * V[i] / D[i] ) << endl;
    }
    return;
}

