#include <iostream>
using namespace std;

typedef long long int LLI;

int n;
LLI d[10];
int v[10];

bool input() {
  cin >> n;
  if (!n) {
    return false;
  }
  for (int i = 0; i < n; ++i) {
    cin >> d[i] >> v[i];
  }
  return true;
}

LLI gcd(LLI a, LLI b) {
  if (a < b) {
    return gcd(b, a);
  }

  while (b) {
    LLI t = a % b;
    a = b;
    b = t;
  }
  return a;
}

LLI lcm(LLI a, LLI b) {
  LLI g = gcd(a, b);
  return a / g * b;
}

void solve() {
  for (int i = 0; i < n; ++i) {
    LLI m = gcd(d[i], v[i]);
    d[i] /= m;
    v[i] /= m;
  }
  LLI g = 1;
  for (int i = 0; i < n; ++i) {
    g = lcm(g, v[i]);
  }
  for (int i = 0; i < n; ++i) {
    d[i] *= g / v[i];
  }
  g = 1;
  for (int i = 0; i < n; ++i) {
    g = lcm(g, d[i]);
  }
  for (int i = 0; i < n; ++i) {
    cout << g / d[i] << endl;
  }
}

int main() {
  while (input()) {
    solve();
  }
  return 0;
}

