using namespace std;

long long int gcd(long long int a, long long int b) {
	if (b == 0) {
		return a;
	} else {
		return gcd(max(b, a%b), min(b, a%b));
	}
}

long long int lcm(long long int a, long long int b) {
	return a * b / gcd(a, b);
}

typedef pair<long long int, long long int> P;

int main() {
	int n;
	while (cin >> n) {
		if (n == 0) {
			break;
		}
		vector< vector<long long int> > hyou(n, vector<long long int>(2));
		vector< vector<long long int> > hyou2(n, vector<long long int>(2));
		P pp;
		priority_queue<P> pq;
		for (int i = 0; i < n; i++) {
			cin >> pp.first >> pp.second;
			hyou[i][0] = pp.first;
			hyou[i][1] = pp.second;
			pq.push(pp);
		}
		for (int i = 0; i < n; i++) {
			P ppp = pq.top();
			pq.pop();
			hyou2[i][0] = ppp.first;
			hyou2[i][1] = ppp.second;
		}
		long long int p = hyou2[0][0];
		for (int i = 1; i < n; i++) {
			for (int j = 1; ; j++) {
				long double k = (long double)(p*j)/(long double)hyou2[0][1];
				long double b = (long double)hyou2[i][1]*k/(long double)hyou2[i][0];
				if (b-(long long int)b <= 1e-12) {
					p *= j;
					break;
				}
			}
		}
		long double kk = (long double)p/(long double)hyou2[0][1];
		for (int i = 0; i < n; i++) {
			cout << (long long int)(kk*hyou[i][1])/hyou[i][0] << endl;
		}
	}
	return 0;
}

#include<bits/stdc++.h>
using namespace std;
typedef unsigned long long ullong;
#define MAX 10
ullong gcd(ullong a, ullong b) {
	return (b == 0) ? a : gcd(b, a % b);
}
ullong lcm(ullong a, ullong b) {
	if (b > a)
		swap(a, b);
	return a / gcd(a, b) * b;
}
void compute(int n) {
	ullong d1, d2, v1, v2, G;
	pair<ullong, ullong> S[MAX];
	for (int i = 0; i < n; i++) {
		cin >> S[i].first >> S[i].second;
		G = gcd(S[i].first, S[i].second);
		S[i].first /= G;
		S[i].second /= G;
	}
	ullong l = lcm(S[0].first, S[1].first);
	for (int i = 2; i < n; i++)
		l = lcm(l, S[i].first);

	ullong A[MAX];
	for (int i = 0; i < n; i++)
		A[i] = S[i].second * (l / S[i].first);
	G = gcd(A[0], A[1]);
	for (int i = 2; i < n; i++)
		G = gcd(G, A[i]);
	for (int i = 0; i < n; i++)
		cout << A[i] / G << endl;
}
main() {
	int n;
	while( cin >> n && n ) compute(n);
}

