		for (int i = 0; i < N; ++i) {
			long long int d, v; cin >> d >> v;
			long long int agcd(gcd(d, v));
			d /= agcd; v /= agcd;
			ds[i] = d; vs[i] = v;
		}
		for (int i = 0; i < N; ++i) {
			for (int j = 0; j < N; ++j) {
				if (i == j)continue;
				ds[i] *= vs[j];
			}
		}
		long long int alca = 1;
		for (int i = 0; i < N; ++i) {
			alca = lca(alca, ds[i]);
		}
		for (int i = 0; i < N; ++i) {
			cout << alca / ds[i] << endl;
		}
	}

	return 0;
}

