typedef pair<ll,ll> pll;


ll gcd(ll a, ll b){
	if(a<b)swap(a,b);

	while(1){
		while(b==0)printf("heee");
		ll c = a%b;

		if(c==0)return b;
		a = b;
		b = c;
	}
	return -1;
}
pll tubun(pll a){
	ll gc = gcd(a.first, a.second);
	a.first/=gc;
	a.second/=gc;
	return a;
}
ll lcm(ll a, ll b, ll c, ll d){
	pll tes1 = tubun(pll(a,b));
	pll tes2 = tubun(pll(c,d));

	a = tes1.first;  b = tes1.second;
	c = tes2.first;  d = tes2.second;


	ll bunbo = b*d;
	ll A = a*d;
	ll B = b*c;
	ll C = gcd(A,B);
	ll bunshi = A/C * B/C * C;

	return bunshi/bunbo;
}
vector<int> solve(){
	vector<int> ret;

	rep(i,n){
		pll hoge = tubun(pll(d[i],s[i]));
		d[i] = hoge.first;
		s[i] = hoge.second;
	}

	ll mygcd = gcd(s[0],s[1]);


	reps(i,2,n){
