long int gcd(long int a,long int b){
    long int	result = 1;
    long int	halfof_min_ab = (( a < b ) ? a : b ) / 2;
    long int	m = 2;
    while( m <= halfof_min_ab ){
        if( !( a % m ) && !( b % m )){
            result = m;
        }
        ++m;
    }
    return	result;
}

long int lcm(long int a,long int b){
    return	( a * b ) / gcd( a, b );
}

