unsigned long long _gcd(unsigned long long m, unsigned long long n) {
    if (m == 0) return n;
    return _gcd(n % m, m);
}

unsigned long long gcd(unsigned long long m, unsigned long long n) {
    if (m < n) return _gcd(m, n);
    else return _gcd(n, m);
}

unsigned long long lcm(unsigned long long m, unsigned long long n) {
    if (m > n)
        return m * n / _gcd(n, m);
    return m * n / _gcd(m, n);
}


