long long GCD(long long m, long long n)
{
	if(n>m){
		long long tmp = m;
		m = n;
		n = tmp;
	}

	if(n==0)
		return m;

	while(true){
		if(m%n==0)
			return n;

		long long tmp = m%n;
		m = n;
		n = tmp;
	}
}

