int main(void){
	int n;
	ll runner[10][2];
	ll change[10];
	ll ans[10];
	while(cin >> n,n){
		rep(i,n) cin >> runner[i][0] >> runner[i][1];
		//gcd
		rep(i,n) {
			ll tmp=gcd(runner[i][0],runner[i][1]);
			runner[i][0]/=tmp;
			runner[i][1]/=tmp;
		}
		// lcm
		ll l=runner[0][1];
		rep(i,n-1) l=lcm(l,runner[i+1][1]);
		rep(i,n) change[i]=l/runner[i][1]*runner[i][0];
		//gcd 
		ll g=change[0];
		rep(i,n-1) g=gcd(g,change[i+1]);
		rep(i,n) change[i]/=g;
		// lcm
		l=change[0];
		rep(i,n-1) l=lcm(l,change[i+1]);
		rep(i,n) ans[i]=l/change[i];
		// gcd
		g=ans[0];
		rep(i,n-1) g=gcd(g,ans[i+1]);
		rep(i,n) ans[i]/=g;

		rep(i,n) cout << ans[i] << endl;
	}
	return 0;
}

