      long long d = D[i], v = V[i], g = gcd(v*d_, v_*d);
      ans[i] = d_*v/g;
      for(int j = 0; j < i; ++j){
        ans[j] *= d*v_/g;
      }
      d_ *= d*v_/g;
    }
    for(int i = 0; i < n; ++i) cout << ans[i] << endl;
  }
  return 0;
}

