typedef pair<ll,ll> pll;

pll tubun(pll a){
	ll mini = min(a.first, a.second);
	for(int i=2;i*i<=mini;i++){
		if(a.first%i==0 && a.second%i==0)
		{
			a.first/=i;
			a.second/=i;
			i--;

			mini = min(a.first, a.second);
		}
	}
	return a;
}

ll gcd(ll a, ll b){
	if(a<b)swap(a,b);

	while(1){
		ll c = a%b;
		if(c==0)return b;
		a = b;
		b = c;
	}
	return -1;
}
ll lcm(ll a, ll b, ll c, ll d){
	pll tes1 = tubun(pll(a,b));
	pll tes2 = tubun(pll(c,d));

	a = tes1.first;  b = tes1.second;
	c = tes2.first;  d = tes2.second;


	ll bunbo = b*d;
	ll A = a*d;
	ll B = b*c;
	ll C = gcd(A,B);
	ll bunshi = A/C * B/C * C;

	return bunshi/bunbo;
}

ll ngcd(ll a, ll b){
	if(a<b)swap(a,b);
	while(1){
		ll c = a%b;
		if(c==0)return b;
		a = b;
		b = c;
	}
	return -1;
}


