	vector<int> ret;

	rep(i,n){
		pll hoge = tubun(pll(d[i],s[i]));
		d[i] = hoge.first;
		s[i] = hoge.second;
	}

	ll mygcd = gcd(s[0],s[1]);
	reps(i,2,n)mygcd = ngcd(mygcd, s[2]);
	rep(i,n)s[i]/=mygcd;

	ll mylcm = lcm(d[0],s[0],d[1],s[1]);
	reps(i,2,n){
		mylcm = lcm(mylcm, 1, d[i], s[i]);
	}

	rep(i,n){
		ret.push_back((int)(mylcm/d[i]*s[i]));
	}

	return ret;
}

void c_solve(){
	vector<int> ans = solve();
	rep(i,ans.size())cout<<ans[i]<<endl;
}

int main(){
	while(input())c_solve();	
}

