			break;
		}
		vector< vector<long long int> > hyou(n, vector<long long int>(2));
		vector< vector<long long int> > hyou2(n, vector<long long int>(2));
		P pp;
		priority_queue< P, vector<P>, greater<P> > pq;
		for (int i = 0; i < n; i++) {
			cin >> pp.first >> pp.second;
			hyou[i][0] = pp.first;
			hyou[i][1] = pp.second;
			pq.push(pp);
