ll gcd(ll a,ll b)
{
	if(a == 0 || b == 0) return 0;

	while(a != b)
	{
		if(a > b) a -= b;
		else b -= a;
	}
	return a;
}

ll lcm(ll a,ll b)
{
	if(a == 0 || b == 0) return 0;
	return ((a / gcd(a,b)*b));
}

