using namespace std;

typedef long long int ll;

ll lcm(ll a,ll b){
    if(a<b)swap(a, b);
    ll gcd=a%b;
    ll x=a*b;
    while (gcd!=0) {
        a=b;
        b=gcd;
        gcd=a%b;
    }
