#include <bits/stdc++.h>
using namespace std;

typedef struct{
	long long nr,dr;
} Frac;


long long gcd(long long a,long long b){
	if(a % b == 0){
		return b;
	}
	return gcd(b,a%b);
}
long long lcm(long long a,long long b){
	long long g = gcd(a,b);
	return a / g * b;
}

Frac lcm_f(Frac f1,Frac f2){
	long long cdr = lcm(f1.dr,f2.dr);
	long long f1t = cdr / f1.dr;
	long long f2t = cdr / f2.dr;
	long long f1n = f1.nr * f1t;
	long long f2n = f2.nr * f2t;
	long long cnr = lcm(f1n,f2n);
	long long g = gcd(cnr,cdr);
	Frac ret;
	ret.nr = cnr / g;
	ret.dr = cdr / g;
	return ret;
}

long long cal(Frac fr,long long d,long long v){
	return fr.nr * v / fr.dr / d;
}

