Int gen(int x){
    Int res;
    res.fill(0);
    for (int i = 2; i <= x; i++){
        if (x%i == 0){
            int c = 0;
            while (x%i == 0)x /= i, c++;
            res[i] = c;
        }
    }
    return res;
}

ll rev(Int CR x){
    ll res = 1;
    for (int i = 0; i < 10000; ++i) {
        for (int j = 0; j < x[i]; ++j) {
            res *= i;
        }
    }
    return res;
}

