#include <iostream>
using namespace std;

unsigned long long gcd(unsigned long long m, unsigned long long n)
{
	if(n>m){
		unsigned long long tmp = m;
		m = n;
		n = tmp;
	}

	if(n==0)
		return m;

	while(true){
		if(m%n==0)
			return n;

		unsigned long long tmp = m%n;
		m = n;
		n = tmp;
	}
}

unsigned long long lcm(unsigned long long m, unsigned long long n)
{
	return m/gcd(m, n)*n;
}

int main()
{
	while(true){
		int n;
		cin >> n;
