			break;

		unsigned long long d[10], v[10];
		for(int i=0; i<n; i++){
			cin >> d[i] >> v[i];
			unsigned long long g = gcd(d[i], v[i]);
			d[i] /= g;
			v[i] /= g;
		}

		unsigned long long D = lcm(d[0], d[1]);
		for(int i=2; i<n; i++)
			D = lcm(D, d[i]);

		for(int i=0; i<n; i++)
			v[i] = D/d[i]*v[i];

		unsigned long long V = gcd(v[0], v[1]);
		for(int i=2; i<n; i++)
			V = gcd(V, v[i]);

		for(int i=0; i<n; i++)
