typedef unsigned long long ull;

ull gcd(ull x, ull y)
{
  if (x < y) {
    swap(x, y);
  }
  ull r;
  while ((r = x % y) != 0) {
    x = y;
    y = r;
  }
  return y;
}

int main()
{
  int N;
  while (cin >> N && N != 0) {
    vector<pair<ull,ull> > v(N);
