typedef long long lli;

lli gcd(lli a, lli b) {
  if(b == 0) return a;
  return gcd(b,a%b);
}

lli lcm(lli a, lli b) {
  return a*b/gcd(a,b);
}

pair<lli,lli> irreducible(pair<lli,lli> p) {
  lli t = gcd(p.first,p.second);
  return make_pair(p.first/t,p.second/t);
}

pair<lli,lli> lcm(pair<lli,lli> a, pair<lli,lli> b) {
  a = irreducible(a);
  b = irreducible(b);
  lli t = lcm(a.second,b.second);
  return irreducible(make_pair(lcm(a.first*t/a.second,b.first*t/b.second),t));
}

