			Lcm[i][k++] = nowLcm / (Distance[i] * Speed[j]);
		}
	}

	for (int i = 0; i < N; ++i) {
		for (int j = 1; j < N - 1; ++j) {
			Lcm[i][0] = findLcm(Lcm[i][0], Lcm[i][j]);
		}
	}

	for (int i = 0; i < N; ++i) {
		cout << Lcm[i][0] << endl;
	}
}

