            if (i == 0) {
                t = pl(d[i], v[i]);
            } else {
                t = lcm(t, pl(d[i], v[i]));
            }
        }
        for (int i=0; i<n; ++i) {
            cout << (t.first * v[i]) / (d[i] * t.second) << endl;
        }
    }
    return 0;
}

