#define N 10
typedef unsigned long long int INT;
INT GCD(INT a, INT b){
	if( b == 0 ) return a;
	else return GCD(b,a%b);
}
INT LCM(INT a, INT b){
	return (a/GCD(a,b))*b;
}
int main(){
	while(1){
		int i,n;
		INT d[N];
		INT v[N];
		INT Rates[N];
		INT lcmV = 1;
		INT lcmRates = 1;

