public class Main {
	MyScanner sc = new MyScanner();
	Scanner sc2 = new Scanner(System.in);
	int mod = 1000000007;

	void run() {
		for (;;) {
			int n = sc2.nextInt();
			if (n == 0) {
				return;
			}
			long[][] info = new long[n][2];
			for (int i = 0; i < n; i++) {
				info[i][0] = sc2.nextLong();
				info[i][1] = sc2.nextLong();

				long div = gcd(info[i][0], info[i][1]);
				info[i][0] /= div;
				info[i][1] /= div;
			}
			long gcd = gcd(info[0][1], info[1][1]);
			long lcm = info[0][1] * info[1][1] / gcd;
			for (int i = 2; i < n; i++) {
				gcd = gcd(lcm, info[i][1]);
				lcm = lcm / gcd * info[i][1];
			}
			for (int i = 0; i < n; i++) {
				info[i][0] *= lcm / info[i][1];
				info[i][1] = lcm;
			}
			gcd = gcd(info[0][0], info[1][0]);
			lcm = info[0][0] / gcd * info[1][0];
			for (int i = 2; i < n; i++) {
				gcd = gcd(lcm, info[i][0]);
				lcm = lcm / gcd * info[i][0];
			}
			for (long[] out : info) {
				System.out.println(lcm / out[0]);
			}
		}
	}

	long gcd(long a, long b) {
		return a % b == 0 ? b : gcd(b, a % b);
	}

