			BigInteger[] d = new BigInteger[n], v = new BigInteger[n];
			d[0] = new BigInteger(sc.next()); v[0] = new BigInteger(sc.next());
			BigInteger nume = new BigInteger("" + d[0]), deno = new BigInteger("" + v[0]);
