				d[i] = new BigInteger(sc.next()); v[i] = new BigInteger(sc.next());
				BigInteger div = gcd(d[i], v[i]); 
				d[i] = d[i].divide(div); v[i] = v[i].divide(div);
				nume = lcm(nume, d[i]);
				deno = gcd(deno, v[i]);
