			int[] v = new int[n];
			for(int i = 0; i < n; i++) {
				d[i] = sc.nextInt();
				v[i] = sc.nextInt();
				int T = (int)gcd(d[i],v[i]);
				d[i] = d[i] / T; v[i] = v[i] / T;
			}
			long dd = lcm(d);
			long vv = gcd(v);

			long T = dd/vv;

			for(int i = 0; i < n; i++) {
				System.out.println((v[i] / vv) * (dd/ d[i]));
			}

		}
	}
}

