using System;

namespace AOJ.Volume2
{
    public class Jogging
    {
        private struct Course
        {
            public int Dist;
            public int Speed;
        }

        public static int Main()
        {
            while (true)
            {
                int n = int.Parse(Console.ReadLine());
                if (n == 0) { break; }

                var student = new Course[n];
                for (int i = 0; i < n; i++)
                {
                    var input = Console.ReadLine().Split(' ');
                    student[i].Dist = int.Parse(input[0]);
                    student[i].Speed = int.Parse(input[1]);
                }
                // 速度v、一周の距離dとすると
                // ある時間tに全員が会う→走った距離(t*v)が一周の距離で割りきれる
                // (t * v) % d == 0が全員について成り立つ(v、dは整数なのでtも整数)
                long t = 0;
                while (true)
                {
                    t++;
                    bool end = true;
                    for (int i = 0; i < n; i++)
                    {
                        if ((student[i].Speed * t) % student[i].Dist != 0)
                        {
                            end = false;
                            break;
                        }
                    }
                    if (end) { break; }
                }

                // 出力
                for (int i = 0; i < n; i++)
                {
                    Console.WriteLine(student[i].Speed * t / student[i].Dist);
                }
            }
            return 0;
        }
    }
}

