const int MOD = 1e9 + 7;
const int N = 3030;

const int INV3 = MOD/3 + 1;
ll inv[N], pot[N], fact[N], ifact[N]; 

vector<ll> unit(int g) {
    vector<ll> ans(g+1);
    ans[g] = ifact[g];
    return ans;
}

vector<ll> gen(int g) {
    vector<ll> ans(g+1);
    ans[0] = pot[g];
    forsn(i,1,g+1) ans[i] = ans[i-1] * INV3 % MOD * inv[i] % MOD;
    return ans;
}

void add(ll &x, ll v) {
    x += v;
    if (x >= MOD) x -= MOD;
}

vector<ll> operator*(const vector<ll> &a, const vector<ll> &b) {
    int n = si(a), m = si(b);
    vector<ll> c(n+m-1);
    forn(i,n) forn(j,m) add(c[i+j], a[i]*b[j]%MOD);
    return c;
}

