#define llint long long
#define mod 1000000007

using namespace std;

llint A, B, C;
llint beki2[300005], beki3[300005];
llint dp[300005];

llint fact[300005], fact_inv[300005];
llint pow(llint a, llint n)
{
	if(n == 0) return 1;
	if(n % 2){
		return ((a%mod) * (pow(a, n-1)%mod)) % mod;
	}
	else{
		return pow((a*a)%mod, n/2) % mod;
	}
}

void make_fact()
{
	llint val = 1;
	fact[0] = 1;
	for(int i = 1; i < 300005; i++){
		val *= i;
		val %= mod;
		fact[i] = val;
	}
	for(int i = 0; i < 300005; i++){
		fact_inv[i] = pow(fact[i], mod-2);
	}
}

llint comb(llint n, llint k)
{
	llint ret = 1;
	ret *= fact[n];
	ret *= fact_inv[k], ret %= mod;
	ret *= fact_inv[n-k], ret %= mod;
	return ret;
}

int main(void)
{
	cin >> A >> B >> C;
	if(B > C) swap(B, C);

	make_fact();

	llint mul = 1;
	for(int i = 0; i < 300005; i++){
		beki2[i] = mul;
		mul *= 2, mul %= mod;
	}
	mul = 1;
	for(int i = 0; i < 300005; i++){
		beki3[i] = mul;
		mul *= 3, mul %= mod;
	}

	for(int i = 0; i <= B; i++) dp[i] = beki2[i];
	for(int i = B; i < C; i++){
		dp[i+1] = (2 * dp[i] + mod - comb(i, B)) % mod;
		dp[i+1] %= mod;
	}
	for(int i = C; i < 300005; i++){
		dp[i+1] = (2 * dp[i] + 2 * mod - (comb(i, i-C) + comb(i, B))) % mod;
		dp[i+1] %= mod;
	}

	llint ans = 0, tmp;
	for(int i = 0; i <= B+C; i++){
		tmp = comb(A+i-1, i) * dp[i] % mod * beki3[B+C-i] % mod;
		ans += tmp, ans %= mod;
	}
	cout << ans << endl;

	return 0;
}

#include <bits/stdc++.h>

using namespace std;

//#define FILE_IO

const int NMAX = 3e5 + 5;
const int mod = 1e9 + 7;

int N, M, K;
int fct[2 * NMAX], ifct[2 * NMAX], p3[2 * NMAX];
int cnt[NMAX];

int power(int x, int y)
{
    if(y == 0)  return 1;
    int ans = power( (1LL * x * x) % mod, y >> 1 );
    if(y & 1)   ans = (1LL * ans * x) % mod;
    return ans;
}

void pre(int N)
{
    fct[0] = 1;
    for(int i = 1; i <= N; i++) fct[i] = (1LL * fct[i - 1] * i) % mod;
    ifct[N] = power(fct[N], mod - 2);
    for(int i = N - 1; i >= 0; i--) ifct[i] = (1LL * ifct[i + 1] * (i + 1)) % mod;

    p3[0] = 1;
    for(int i = 1; i <= N; i++) p3[i] = (3LL * p3[i - 1]) % mod;
}

int C(int N, int K)
{
    if(N < 0 || K < 0 || N < K)  return 0;
    int ans = fct[N];
    ans = (1LL * ans * ifct[K]) % mod;
    ans = (1LL * ans * ifct[N - K]) % mod;
    return ans;
}

int main()
{
    #ifdef FILE_IO
    freopen("1.in", "r", stdin);
    freopen("1.out", "w", stdout);
    #endif

    pre(2 * NMAX);

    scanf("%d%d%d", &N, &M, &K);

    cnt[0] = 1;
    int lstst = 0, lstdr = 0, lstans = 1;
    for(int i = 1; i <= M + K; i++)
    {
        int st = max(0, i - K);
        int dr = min(M, i);
        int ans = (2LL * lstans + C(i - 1, lstst - 1) - C(i - 1, lstdr)) % mod;
        while(lstst < st)
        {
            ans = (ans - C(i, lstst) + mod) % mod;
            lstst++;
        }
        lstdr++;
        while(lstdr <= dr)
        {
            ans = (ans + C(i, lstdr)) % mod;
            lstdr++;
        }

        lstst = st, lstdr = dr, lstans = ans;
        cnt[i] = ans;
    }

    int ans = 0;
    for(int i = N; i <= N + M + K; i++)
    {
        int sol = (1LL * C(i - 1, N - 1) * cnt[i - N]) % mod;
        sol = (1LL * sol * p3[N + M + K - i]) % mod;
        ans = (ans + sol) % mod;
    }

    printf("%d\n", ans);

    return 0;
}

