}

ll power( ll a , ll b ) {
    int ans = 1 , base = a ;
    while( b ) {
        if( b&1 ) ans = mul( ans , base ) ;
        base = mul( base , base ) ; 
        b >>= 1 ;
    }
    return ans ;
}

ll inv( ll x ) {
    return power( x , mod - 2 ) % mod ;
}

ll C( ll x , ll y ) {
