struct base {

    double a, b;

    base() : a(0), b(0) {}

    base(double a, double b = 0) : a(a), b(b) {}

};

inline base operator + (const base& a, const base& b) {
    return base(a.a + b.a, a.b + b.b);
}

inline base operator - (const base& a, const base& b) {
    return base(a.a - b.a, a.b - b.b);
}

inline base operator * (const base& a, const base& b) {
    return base(a.a * b.a - a.b * b.b, a.a * b.b + a.b * b.a);
}

inline base operator / (const base& a, double b) {
    return base(a.a / b, a.b / b);
}

