ll Pow(ll b, ll e){
	ll ret = 1;
	while(e){
		if(e % 2 == 1) ret = ret * b % mod;
		e /= 2;
		b = b * b % mod;
	}
	return ret;
}

void Build(){
	fact[0] = finv[0] = pow3[0] = 1;
	for(int i=1; i<=1'000'000; ++i){
		fact[i] = fact[i-1] * i % mod;
		finv[i] = Pow(fact[i], mod - 2);
		pow3[i] = pow3[i-1] * 3 % mod;
	}
}

ll Comb(ll n, ll r){
	if(n<0 || r<0 || r>n) return 0;
	return fact[n] * finv[r] % mod * finv[n-r] % mod;
}

int main()
{
	Build();
	cin >> A >> B >> C;
	ll ans = 0;
	ll v3 = 0;
	for(ll s = 0; s <= B+C; ++s){
		ll v1 = Comb(A+s-1, A-1);
		ll v2 = pow3[B+C-s];
		if(s == 0) v3 = 1;
		else {
			v3 = v3 * 2;
			v3 += mod - Comb(s-1, C);
			if(s >= B) v3 += mod - Comb(s-1, B);
			v3 %= mod;
		}
		ll t;
		ans += t = v1 * v2 % mod * v3 % mod;
		ans %= mod;
	}
