int fact[N], inv_fact[N], pw[N];

int binPow (int x, int y) {
    int ans = 1;
    while (y) {
        if (y & 1) ans = (ans * x) % mod;
        x = (x * x) % mod;
        y >>= 1;
    }

    return ans;
}

int C (int n, int k) {
    int ans = 1;
    ans *= fact[n]; ans %= mod;
    ans *= inv_fact[k]; ans %= mod;
    ans *= inv_fact[n - k]; ans %= mod;

    return ans;
}

signed main(){
    fact[0] = inv_fact[0] = pw[0] = 1;
    for (int i = 1; i < N; i++) {
        pw[i] = (pw[i - 1] * 3) % mod;
        fact[i] = (i * fact[i - 1]) % mod;
        inv_fact[i] = binPow(fact[i], mod - 2);
    }

    int n, m, k;
    scanf("%lld %lld %lld", &n, &m, &k);
    int ans = 0;

    for (int i = 0; i <= m; i++) {
        for (int j = 0; j <= k; j++) {
            ans = (ans + C(i + j, i) * C(i + j + n - 1, i + j) % mod * pw[m + k - i - j]) % mod;
        }
    }

    printf("%lld", ans);
    return 0;
}

