  lli n, m, k; cin >> n >> m >> k;
  p3[0] = 1; FOR(i, 1000000) p3[i+1]=(3*p3[i])%MOD;
  FOR(i, 3000) {
    cnk[i][0] = 1;
    cnk[i][i] = 1;
    FORU(j, 1, i-1) cnk[i][j] = (cnk[i-1][j-1]+cnk[i-1][j])%MOD;
  }
  lli ans = 0;
  lli cur0 = 1;
  lli l0 = 0, l1 = 0;
  lli v0 = 1, v1 = 1;
  lli s0 = 0;
  FORU(i, n, n+m+k) {
    lli nl0 = max<lli>(0, i-n-m);
    lli nl1 = min<lli>(i-n, k);
    lli nv0, nv1;
    lli nv00, nv01;
    lli nv10, nv11;
    nv00 = (v0+((v0*(i-1-n-l0))%MOD)*invmod(nl0))%MOD;
    nv00 += MOD; nv00 %= MOD;
    nv01 = 1;
    if(nl0>l0) {
      nv0 = nv00;
    }else{
      nv0 = nv01;
    }
    nv10 = (v1+((v1*(i-1-n-l1))%MOD)*invmod(nl1))%MOD;
    nv10 += MOD; nv10 %= MOD;
    nv11 = (v1+((v1*invmod(i-n-l1))%MOD)*(l1))%MOD;
    nv11 += MOD; nv11 %= MOD;
    if(nl1>l1){
      nv1 = nv10;
    }else{
      nv1 = nv11;
    }
    if(nl0==nl1) s0 = 0;
    else if(nl0+1==nl1) {
      s0=nv1;
    } else {
      lli ns0 = s0+s0-v1+v0;
      ns0%=MOD;
      if(nl1>l1) ns0 += nv1;
      if(nl0>l0) ns0 -= nv0;
      ns0 %= MOD;
      ns0 += MOD;
      ns0 %= MOD;
      s0 = ns0;
    }
    // FORU(j, nl0, nl1) {
    //   s += cnk[i-n][j];
    // }
    lli s = nv0+s0;
    l0 = nl0; l1 = nl1;
    v0 = nv0; v1 = nv1;
    s %= MOD;
    s *= cur0;
    s %= MOD;
    s *= p3[n+m+k-i];
    s %= MOD;
    (ans += s) %= MOD;

    cur0 = cur0+((cur0*invmod(i-n+1))%MOD)*(n-1); cur0 %= MOD; cur0 += MOD; cur0 %= MOD;
  }
  cout << ans << endl;
  return 0;
}

