    int sum = 1;
    int l = 0;
    int r = 0;
    ans += 1LL * exp(3, M + K) % mod * sum % mod;
    ans %= mod;

    for(int i = M + K - 1; i >= 0; i--) {
        sum *= 2;
        sum %= mod;
        sum += comb(M + K - i - 1, l - 1);
        sum %= mod;
        sum += mod - comb(M + K - i - 1, r);
