    vector<int> pw3(m + k + 1);
    pw3[0] = 1;
    for (int i = 1; i <= m + k; ++i) {
        pw3[i] = pw3[i - 1];
        ModMul(pw3[i], 3);
    }

    int l = 0;
    int r = 0;
    int sum = 1;

    int result = 0;
    for (int s = 0; s <= m + k; ++s) {
        const int cl = max(0, s - k);
        const int cr = min(m, s);

        if (l == r) {
            sum = 0;
            for (int i = cl; i <= cr; ++i) {
                ModSum(sum, GetC(s, i));
            }
            l = cl;
            r = cr;
        } else {
            ModMul(sum, 2);
            ModSum(sum, -GetC(s - 1, l));
            ModSum(sum, -GetC(s - 1, r));
            ++l;

            while (r < cr) {
                ModSum(sum, GetC(s, r + 1));
                ++r;
            }
            while (l > cl) {
                ModSum(sum, GetC(s, l - 1));
                --l;
            }

            while (r > cr) {
                ModSum(sum, -GetC(s, r));
                --r;
            }
            while (l < cl) {
                ModSum(sum, -GetC(s, l));
                ++l;
            }
        }

        int curResult = GetC(s + (n - 1), n - 1);
        ModMul(curResult, pw3[m + k - s]);
        ModMul(curResult, sum);

        ModSum(result, curResult);
    }

    cout << result << endl;

    return 0;
}

