using namespace std;

int Fact[1000005],P3[1000005],Inv[1000005],Dp[1000005];

int put(int a, int b)
{
    int rez=1;
    for(int i=0; (1<<i)<=b; i++)
    {
        if((1<<i)&b)
            rez=(1LL*rez*a)%MOD;
        a=(1LL*a*a)%MOD;
    }
    return rez;
}

int comb(int n, int k)
{
    if(k>n)
        return 0;

    int rez=(1LL*Fact[n]*Inv[k])%MOD;
    rez=(1LL*rez*Inv[n-k])%MOD;
    return rez;
}

int main()
{
	ios::sync_with_stdio(false);
