    {
        Dp[i]=(Dp[i-1]-comb(i-1,m)+MOD)%MOD; //pun pe poz i B
        Dp[i]=(1LL*Dp[i]+Dp[i-1]-comb(i-1,k)+MOD)%MOD; //pun pe poz i C

        if(Dp[i]==0)
